import React from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';

import axios from 'axios';

class EditComponent extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      campName: "",
      campEmail:"",
      campAmount:"",
      campRecordType:"",
      selectRole:0
    }
  }

  render(){
    return (
      <div>
        <div class="form-row justify-content-center">
          <div class="form-group col-md-6">
            <label for="inputPassword4">Name </label>
            <input type="text" class="form-control"  placeholder="Name" value={this.state.campName} onChange={(value)=> this.setState({campName:value.target.value})}/>
          </div>
          <div class="form-group col-md-6">
            <label for="inputEmail4">Email</label>
            <input type="email" class="form-control"  placeholder="Email" value={this.state.campEmail} onChange={(value)=> this.setState({campEmail:value.target.value})}/>
          </div>
        </div>
        <div class="form-row">
          <div class="form-group col-md-6">
            <label for="inputState">Role</label>
            <select id="inputState" class="form-control" onChange={(value)=> this.setState({selectRole:value.target.value})}>
              <option selected>Choose...</option>
              <option value="1">food</option>
              <option value="2">clothes</option>
              <option value="3">supermarket</option>
            </select>
          </div>
          <div class="form-group col-md-6">
            <label for="inputEmail4">Amount</label>
            <input type="number" class="form-control"  placeholder="Argentine pesos"  value={this.state.campAmount} onChange={(value)=> this.setState({campAmount:value.target.value})}/>
          </div>
        </div>
        <div class="form-group">
          <label for="inputAddress">Record Type</label>
          <input type="text" class="form-control" id="inputAddress" placeholder="Entry or Exit" value={this.state.campRecordType} onChange={(value)=> this.setState({campRecordType:value.target.value})}/>
        </div>
        <button type="submit" class="btn btn-primary" onClick={()=>this.sendSave()}>Save</button>
      </div>
    );
  }

  sendSave(){

    /// por si no ha seleccionado nada de role
    if (this.state.selectRole==0) {
      alert("Seleccione el tipo de Role")
    }
    else if (this.state.campAmount=="") {
        alert("Enter the amount field")
    }
    else if (this.state.campName=="") {
        alert("Enter the name field")
    }
    else if (this.state.campEmail=="") {
        alert("Enter the email field")
    }
    else if (this.state.campRecordType=="") {
        alert("Enter the RecordType field")
    }
    else {

      // url de backend
      const baseUrl = "http://192.168.0.5:3000/customer/create"

      // parametros de datos post
      const datapost = {
        name : this.state.campName,
        email : this.state.campEmail,
        phone : this.state.campAmount,
        address : this.state.campRecordType,
        role  : this.state.selectRole
      }

      axios.post(baseUrl,datapost)
      .then(response=>{
        if (response.data.success===true) {
          alert(response.data.message)
        }
        else {
          alert(response.data.message)
        }
      }).catch(error=>{
        alert("Error 34 "+error)
      })

      }

    }
  } 
 export default EditComponent;
