import React from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import axios from 'axios';
const baseUrl = "http://localhost:3000"

class EditComponent extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      dataCustomer:{},
      campName: "",
      campEmail:"",
      campAmount:"",
      campRecordType:"",
      stringRole:"",
      selectRole:0
    }
  }

  componentDidMount(){
    // parametro de id del usuario
    let userId = this.props.match.params.id;
    // http://localhost:3000/customer/get/4
    const url = "http://192.168.0.5:3000/customer/get/4"
    axios.get(url)
    .then(res=>{
      if (res.data.success) {
        const data = res.data.data[0]
        this.setState({
          dataCustomer:data,
          campName: data.name,
          campEmail:data.email,
          campAmount:data.amount,
          campRecordType:data.recordType,
          stringRole:data.role.role,
          selectRole:data.roleId
        })
      }
      else {
        alert("Error web service")
      }
    })
    .catch(error=>{
      alert("Error server "+error)
    })

  }

  render(){
    let userId = 0;
    //let userId = this.props.match.params.employeeId;
    return (
      <form>
        <div class="form-row justify-content-center">
          <div class="form-group col-md-6">
            <label for="inputPassword4">Name {userId}</label>
            <input type="text" class="form-control"  placeholder="Name"/>
          </div>
          <div class="form-group col-md-6">
            <label for="inputEmail4">Email</label>
            <input type="email" class="form-control"  placeholder="Email"/>
          </div>
        </div>
        <div class="form-row">
          <div class="form-group col-md-6">
            <label for="inputState">Role</label>
            <select id="inputState" class="form-control">
              <option selected>Choose...</option>
              <option>...</option>
            </select>
          </div>
          <div class="form-group col-md-6">
            <label for="inputEmail4">Amount</label>
            <input type="number" class="form-control"  placeholder="Argentine Pesos"/>
          </div>
        </div>
        <div class="form-group">
          <label for="inputAddress">RecordType</label>
          <input type="text" class="form-control" id="inputAddress" placeholder="Entry or Exit"/>
        </div>

        <button type="submit" class="btn btn-primary">Sign in</button>
      </form>
    );
  }
 }


export default EditComponent;
