import React from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import axios from 'axios';

import { Link } from "react-router-dom";

//library sweetalert
import Swal from 'sweetalert2/dist/sweetalert2.js'
import 'sweetalert2/src/sweetalert2.scss'
import { data } from 'jquery';

//const baseUrl = "http://localhost:3000"

  class listComponent extends React.Component  {

    constructor(props){
      super(props);
      this.state = {
        listCustomer:[]
      }
    }

    componentDidMount(){

      const url = "http://192.168.0.5:3000/customer/list"
      axios.get(url)
      .then(res=>{
        if (res.data.success) {
          const data = res.data.data
          this.setState({listCustomer:data})
        }
        else {
          alert("Error web service")
        }
      })
      .catch(error=>{
        alert("Error server "+error)
      })

    } 

  render()
  {
    return (
      <table class="table table-hover table-striped">
        <thead class="thead-dark">
          <tr>
            <th scope="col">#</th>
            <th scope="col">Role</th>
            <th scope="col">Name</th>
            <th scope="col">Email</th>
            <th scope="col">Amount</th>
            <th scope="col">RecordType</th>
            <th colspan="2">Action</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <th>1</th>
            <td>Admin</td>
            <td>John Smitth</td>
            <td>jhonsmith@mail.com</td>
            <td>3000</td>
            <td>Entry</td>
            <td>
             <Link class="btn btn-outline-info "  to={"/edit/"+data.id} >Edit</Link>
            </td>
            <td>
              <button class="btn btn-outline-danger" onClick={()=>this.onDelete(data.id)}> Delete </button>
            </td>
          </tr>
          {this.loadFillData()}
        </tbody>
      </table>
    );
  }

  loadFillData(){

    return this.state.listCustomer.map((data)=>{
      return(
        <tr>
          <th>{data.id}</th>
          <td>{data.role.role}</td>
          <td>{data.name}</td>
          <td>{data.email}</td>
          <td>{data.amount}</td>
          <td>{data.recordType}</td>
          <td>
            <Link class="btn btn-outline-info" to={"/edit/"+data.id}>Edit</Link>
          </td>
          <td>
            <button class="btn btn-outline-danger" onClick={()=>this.onDelete(data.id)}> Delete </button>
          </td>
        </tr>
      )
    })
  }

  onDelete(id){
    alert("ID "+id)
  }

}

export default listComponent;
