import React from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';

class EditComponent extends React.Component{

  constructor(props){
   super(props);
   this.state = {
     campName: "",
     campEmail:"",
     campAmount:"",
     campRecordType:"",
     selectRole:0
   }
 }

  render(){

    return (
      <form>
        <div class="form-row justify-content-center">
          <div class="form-group col-md-6">
            <label for="inputPassword4">Name {userId}</label>
            <input type="text" class="form-control"  placeholder="Name"/>
          </div>
          <div class="form-group col-md-6">
            <label for="inputEmail4">Email</label>
            <input type="email" class="form-control"  placeholder="Email"/>
          </div>
        </div>
        <div class="form-row">
          <div class="form-group col-md-6">
            <label for="inputState">Role</label>
            <select id="inputState" class="form-control">
              <option selected>Choose...</option>
              <option>...</option>
            </select>
          </div>
          <div class="form-group col-md-6">
            <label for="inputEmail4">Amount</label>
            <input type="number" class="form-control"  placeholder="Argentine pesos"/>
          </div>
        </div>
        <div class="form-group">
          <label for="inputAddress">RecordType</label>
          <input type="text" class="form-control" id="inputAddress" placeholder="Entry or Exit"/>
        </div>
        <div>
          <button type="submit" class="btn btn-primary" onClick={()=>this.sendSave()}>Save</button>
        </div>
      </form>
    );
  }
 }


 export default EditComponent;
